from collections import deque
import time
import random

def missingDigits(config, x, y):
    # build neighbor map for unitâ€digit transitions
    nbr = {}
    for u in range(10):
        a = (u + x) % 10
        b = (u + y) % 10
        nbr[u] = [a] if a == b else sorted([a, b])

    # precompute shortest non-zero paths between any two digits
    paths = [[None]*10 for _ in range(10)]
    for u in range(10):
        seen = [False]*10
        dq = deque()
        # seed with oneâ€step moves only
        for v in nbr[u]:
            if not seen[v]:
                seen[v] = True
                paths[u][v] = [v]
                dq.append(v)
        # BFS to find shortest paths (length â‰¥ 1)
        while dq:
            cur = dq.popleft()
            for v in nbr[cur]:
                if not seen[v]:
                    seen[v] = True
                    paths[u][v] = paths[u][cur] + [v]
                    dq.append(v)
        # note: paths[u][u] remains None unless there's a cycle back to u

    if not config:
        return ""

    # first character: from 0 to config[0]
    seq = []
    first = int(config[0])
    p0 = paths[0][first]
    if p0 is None:
        return "-1"
    seq.extend(p0)
    cur = first

    # the rest: from cur to each next config digit
    for ch in config[1:]:
        t = int(ch)
        p = paths[cur][t]
        if p is None:
            return "-1"
        seq.extend(p)
        cur = t

    # build final string
    return "".join(map(str, seq))

